import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';


function Lobby({socket, name, lobby}) {
	const [currentBet, setCurrentBet] = useState(0);
	const [betList, setBetList] = useState([]);
    const sockets = io.in({lobby}).fetchSockets();
	
	// don't send empty messages, then transmit message data to other dude
	const submitBet = async (e) => {
		if (parseInt(e) !== 0 && e !== null) {
			const betData = {
                lobby: lobby,
                name: name,
				bet: parseInt(e),
			};

			await socket.emit("submit_bet", betData);
			setBetList((list) => [...list, betData]); // so we can see our own messages
			setCurrentBet(0);
		}
	};

	useEffect (() => {
		socket.on("get_update", (data) => {
			setBetList((list) => [...list, data]);
		})

		return () => socket.removeListener('get_update');
	}, [socket]);

	return (
		<div className="lobby">
			<div className="header">
				<p>{lobby}: Poker Lobby</p>
			</div>

            <div className="body">

				{betList.map((betContent) => {
					return (
						<div>
                            <p>{betContent.name} has bet {betContent.bet}</p>
						</div>
					);
				})}
			</div>

            <div className="betTable">
                <table style="width:100%">
                    
                </table>
            </div>

			<div className="footer">
                <button 
                    onClick={() => {
                        submitBet(1);
                    }}
                >1</button>
                <br/>
                <h3>or enter a custom bet:</h3>
                <br/>
                <input 
                    type="number"
                    placeholder="Enter your bet" 
                    value={currentBet}
                    onChange={(event) => {
                        setCurrentBet(event.target.value);
                    }}
                />
				<button onClick={() => {
                    submitBet(currentBet);
                }}
                >Submit Bet</button>

			</div>
		</div>
	);
}

export default Lobby;
