import React, { useEffect, useState } from 'react';

function Lobby({socket, name, money, lobby}) {
	const [currentBet, setCurrentBet] = useState(0);
    const [cumulativeCurrentBet, setCumulativeCurrentBet] = useState(0);
	const [betList, setBetList] = useState([]);
    const [userList, setUserList] = useState([]);
    const [moneyList, setMoneyList] = useState([]);
	
	// don't send empty data, then transmit data to other dude
	const submitBet = async (e) => {
		if (parseInt(e) !== 0 && e !== null) {
			const betData = {
                lobby: lobby,
                name: name,
				bet: parseInt(e),
                cumulative: cumulativeCurrentBet
			};

			await socket.emit("submit_bet", betData);
			setBetList((list) => [...list, betData]); // so we can see our own bets
			setCurrentBet(0);
		}
	};

	useEffect (() => {
		socket.on("get_update", (data) => {
			setBetList((list) => [...list, data]);
		})

        socket.on("user_joined", (data) => {
            setUserList((list) => data.players);
            setMoneyList((list) => data.money);
        });

		return () => {
            socket.removeListener('get_update');
            socket.removeListener('user_joined');
        }
	}, [socket]);

    function sumOfPot() {
        let sum = 0;
        for (const element of betList) {
            sum = sum + element.bet;
        }
        return sum;
    }

	return (
		<div className="lobby">
			<div className="header">
				<p>{lobby}: Poker Lobby</p>
			</div>

            <div className="body">

				{betList.map((betContent) => {
					return (
						<div>
                            <p>{betContent.name} has bet {betContent.bet}. This round, {betContent.name} has bet {betContent.cumulative}</p>
						</div>
					);
				})}

                <h3>Total Pot: {sumOfPot()} </h3>
			</div>

            <div className="betTable">
                <table>
                    <tr>
                    <th>Players</th>
                    {userList.map((users) => {
                        return (
                            <td>{users}</td>
                        );
                    })}
                    </tr>
                    <tr>
                    <th>Money</th>
                    {moneyList.map((money) => {
                        return (
                            <td>{money}</td>
                        );
                    })}
                    </tr>
                </table>
            </div>

			<div className="footer">
                <button 
                    onClick={() => {
                        setCumulativeCurrentBet(cumulativeCurrentBet + 1);
                        submitBet(1);
                    }}
                >1</button>
                <br/>
                <h3>or enter a custom bet:</h3>
                <br/>
                <input 
                    type="number"
                    placeholder="Enter your bet" 
                    value={currentBet}
                    onChange={(event) => {
                        setCurrentBet(event.target.value);
                    }}
                />
				<button onClick={() => {
                    setCumulativeCurrentBet(cumulativeCurrentBet + parseInt(currentBet));
                    submitBet(currentBet);
                }}
                >Submit Bet</button>

			</div>
		</div>
	);
}

export default Lobby;
